    <script>
        const optionMenu = document.querySelector(".select-menu"),
            selectBtn = optionMenu.querySelector(".select-btn"),
            options = optionMenu.querySelectorAll(".option"),
            sBtn_text = optionMenu.querySelector(".sBtn-text");

        // Variable para almacenar la opción seleccionada
        let selectedOption = null;

        // Abre o cierra el menú de selección al hacer clic en el botón
        selectBtn.addEventListener("click", () => {
            optionMenu.classList.toggle("active");
            // Si el menú está abierto y no se ha seleccionado ninguna opción, muestra el texto por defecto
            if (!selectedOption) {
                sBtn_text.innerText = "Seleccionar tipo de producto";
            }
        });

        // Cambia el texto del botón al seleccionar una opción
        options.forEach((option) => {
            option.addEventListener("click", () => {
                // Elimina la clase "selected" de todas las opciones
                options.forEach((opt) => opt.classList.remove("selected"));

                // Agrega la clase "selected" a la opción seleccionada
                option.classList.add("selected");

                // Captura el texto de la opción seleccionada
                selectedOption = option.querySelector(".option-text").innerText;
                sBtn_text.innerText = selectedOption;

                // Cierra el menú después de seleccionar una opción
                optionMenu.classList.remove("active");
            });
        });

        // Cerrar el select si se hace clic fuera del contenedor
        document.addEventListener("click", (event) => {
            if (
                !optionMenu.contains(event.target) &&
                !selectBtn.contains(event.target)
            ) {
                optionMenu.classList.remove("active");
            }
        });
    </script>
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Selecciona todos los botones con la clase .button-fruta
            var botones = document.querySelectorAll('.button-fruta');

            // Iterar sobre cada botón
            botones.forEach(function(boton) {
                boton.addEventListener('click', function() {
                    // Obtener el ID del producto desde el atributo data-id-product
                    var productId = this.getAttribute('data-id-product');

                    // Construir la URL del endpoint (ajusta la URL a la que corresponda en tu sitio)
                    var endpointUrl = 'http://cahavin.test/wp-json/myapi/v1/producto/' + productId;

                    // Hacer la solicitud fetch al endpoint
                    fetch(endpointUrl)
                        .then(response => {
                            if (!response.ok) {
                                throw new Error('Error en la respuesta del servidor');
                            }
                            return response.json();
                        })
                        .then(data => {
                            console.log('Datos del producto:', data);

                            // Obtenemos el array de estacionalidad y el icono del producto
                            const estacionalidad = data.estacionalidad; // ej: ["ene","feb","mar","abr","may","nov","dic"]
                            const icono = data.icono; // URL de la imagen del icono

                            // Seleccionamos el contenedor de los meses
                            const mesesDiv = document.getElementById('meses');

                            // Seleccionamos cada bloque que representa un mes
                            const monthItems = mesesDiv.querySelectorAll('.items-center.flex.flex-col');

                            const imagenFondo = document.getElementById('imagen-fondo');

                            // Suponiendo que ya tienes el objeto data con los datos del producto
                            const tituloPrincipalElem = document.getElementById('titulo-principal');
                            const titulo2Elem = document.getElementById('titulo-2');

                            const imagenPaquete = document.getElementById('imagen-paquete');

                            if (tituloPrincipalElem && data.titulo) {
                                tituloPrincipalElem.textContent = data.titulo;
                            }

                            if (titulo2Elem && data.titulo_2) {
                                titulo2Elem.textContent = data.titulo_2;
                            }

                            if (imagenFondo && data.seccion_1_imagen_1) {
                                imagenFondo.src = data.seccion_1_imagen_1;
                            }

                            if (imagenPaquete && data.paquete) {
                                imagenPaquete.src = data.paquete;
                            }

                            // Iteramos por cada mes
                            monthItems.forEach(item => {
                                const monthSpan = item.querySelector('span'); 
                                const monthName = monthSpan.textContent.trim().toLowerCase(); 
                                // monthName será algo como "ene", "feb", etc. en minúsculas.

                                const monthDiv = item.querySelector('div'); 
                                // Este es el div que contiene la imagen o estado visual del mes.

                                // Verificamos si el mes está en el array de estacionalidad
                                if (estacionalidad.includes(monthName)) {
                                    // Mes en temporada: mostrar icono y remover clases "no-estacion"
                                    monthDiv.classList.remove('no-estacion-bg-orange');
                                    monthSpan.classList.remove('no-estacion-txt-orange');

                                    // Asignar el icono desde data.icono
                                    monthDiv.innerHTML = `<img src="${icono}" alt="" class="w-[12px] md:w-[24px] xl:w-[36px] h-[12px] md:h-[24px] xl:h-[36px]" />`;
                                } else {
                                    // Mes no en temporada: aplicar clases "no-estacion" y quitar icono
                                    monthDiv.classList.add('no-estacion-bg-orange');
                                    monthSpan.classList.add('no-estacion-txt-orange');

                                    // Quitar cualquier imagen existente
                                    monthDiv.innerHTML = '';
                                }
                            });

                            // Este código iría dentro del bloque .then(data => {...}) donde ya tienes acceso a la variable 'data'
                            const presentacionesElem = document.getElementById('presentaciones');

                            // Limpiar las presentaciones existentes
                            presentacionesElem.innerHTML = '';

                            // Verificar si existe el campo 'presentaciones' y contiene elementos
                            if (data.presentaciones && data.presentaciones.length > 0) {
                                data.presentaciones.forEach(p => {
                                    // Crear el contenedor principal
                                    const container = document.createElement('div');
                                    container.className = 'relative w-[230px] h-[200px] mx-auto';

                                    // Crear la imagen
                                    const img = document.createElement('img');
                                    img.src = p.imagen;
                                    img.alt = p.texto || ''; 
                                    img.className = 'object-cover w-full h-full rounded-[20px]';

                                    // Crear el span con el texto
                                    const span = document.createElement('span');
                                    span.textContent = p.texto;
                                    span.className = 'text-[20px] font-medium absolute bottom-[15px] left-[15px] text-white';

                                    // Añadir la imagen y el texto al contenedor
                                    container.appendChild(img);
                                    container.appendChild(span);

                                    // Añadir el contenedor al div de presentaciones
                                    presentacionesElem.appendChild(container);
                                });
                            } else {
                                // Si no hay presentaciones, el div queda vacío.
                                // Puedes dejarlo así, o agregar un mensaje, o simplemente no hacer nada.
                                presentacionesElem.innerHTML = '';
                            }
                        })
                        .catch(error => {
                            console.error('Error al obtener el producto:', error);
                        });
                });
            });
        });
    </script>
